package operator;

public class AssignmentOperator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          //= -=  += /= %= *=
		int a=5;
		
		a+=2;
		System.out.println(a);
		
		a-=2;
		System.out.println(a);
		
		a*=2;
		System.out.println(a);
		
		a/=2;
		System.out.println(a);
		
		a%=2;
		System.out.println(a);
		
		//int b=c=6; cannot do in java
	}

}
