package operator;

public class ComparisonOperator03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=8;
		//result of comparison is boolean type
		
		//number comparison
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a>=b);
		System.out.println(a<=b);
		System.out.println(a==b);
		System.out.println(a!=b);
		//System.out.println(a>b>c);cannot do in java
		
		System.out.println();
		
		//char comparison can do, compare ascii code
		System.out.println('a'>'b');
	}

}
