package operator;

public class NumberSystem08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*binary prefix0b 0b10
		 * 
		 *  octonary  prefix0 010
		 *  
		 *  hex prefix0x  0x10 
		 */
		
		int a=10;
		int b=0b10;//binary
		int c=010;
		int d=0x10;
		
		System.out.println(a);
		System.out.println(b);//0b10 -> decimal: 2  java prints decimal automatically
		System.out.println(c);//010-> decimal:8
		System.out.println(d);//0x10->hex:16
		
	}

}
